var cutmeGoogleMap = []

function guidGenerator(){
  var S4 = function(){
    return (((1+Math.random())*0x10000)|0).toString(16).substring(1);
  };
  return (S4()+S4()+"-"+S4()+"-"+S4()+"-"+S4()+"-"+S4()+S4()+S4());
}

function initCarousel(pointer = null){
  var carousel = $("ul.list")
  if(pointer) carousel = pointer
  carousel.each(function(){
    if($(this).find("li.item").length > 1){
      $(this).owlCarousel({
        margin:10,
        responsive:{
          0:{
            items:1
          },
          600:{
            items:3
          },
          1000:{
            items:5
          }
        }
      })
    }
  })
}

function initMap(){
  var map = $(".google-map > .map-block")
  map.each(function(){
    if(!$(this).attr("id"))
      $(this).attr("id", guidGenerator())
    var mapCanvas = document.getElementById($(this).attr("id"));
    var mapOptions = {
      center: new google.maps.LatLng(-6.218096, 106.802711),
      panControl: false,
      zoomControl: true,
      mapTypeControl: false,
      scaleControl: false,
      streetViewControl: false,
      overviewMapControl: false,
      rotateControl: false,
      zoom: 15
    }
    cutmeGoogleMap[`${$(this).attr("id")}`] = new google.maps.Map(mapCanvas, mapOptions);
  })
}

function initModal(){
  $(".modal.wr-vertical-align > .wr-outer > .wr-inner").on("click", function(e){
    if(e.target != this) return;
    $(this).closest(".modal").modal("hide");
  });
}

$(function(){
  initModal()
})
