function initSignUp(){
  $("#form-signup").on("submit", function(event){
    event.preventDefault()
    $.post({
      url:`http://localhost:8080/api/customers/`,
      data:{
        "fullName":$("#txt-fullname").val().trim(),
        "email":$("#txt-email").val().trim(),
        "username":$("#txt-username").val().trim(),
        "password":$("#txt-password").val().trim(),
        "address":$("#txt-address").val().trim(),
        "zipcode":$("#txt-zipcode").val().trim(),
        "phone":$("#txt-phone").val().trim()
      },
      success:function(result){
        alert("Data saved!")
      }
    })
  })
}

$(function(){
  initSignUp()
})
